  /**
   * Gets the value for a given key
   * @param {String} key
   * @returns
   */
  async get(key) {
    const getAsync = promisify(this.client.get).bind(this.client);
    const value = await getAsync(key);
    return value;
  }

  /**
   * Sets a key with its value in redis and adds expiration duration
